(8,	'arrumar rotas e kms',	'UPDATE tbl_chamado_roteiro\r\n  SET km_percurso = 30\r\n   --Select * from tbl_chamado_roteiro\r\nWHERE \r\n     --CLIENTE.N_CD_CODIGO = 8063\r\n     data_solicitacao =  \'2021-05-15\'\r\n--	--AND CLIENTE.N_CD_FILIAL = 1\r\n--	--AND CLIENTE.S_DSC_TPCLIENTE = \'C\'\r\n   and id_empresa = 322\r\n    --AND cha.nome_substituto = \'JOSE ANSELMO\'\r\n   AND id_chamado_roteiro = 153048015\r\n\r\n\r\nSELECT\r\n	CLIENTE.S_DSC_FANTASIA,\r\n	CONVERT(VARCHAR,roteiro.data_solicitacao,103) AS Data,\r\n	roteiro.id_chamado_roteiro,\r\n	roteiro.id_chamado,\r\n	cha.nome_substituto,\r\n	roteiro.km_percurso AS Km\r\n	\r\n	\r\nFROM [SGI].[dbo].[TBL_CLIENTE] AS CLIENTE\r\n	\r\n	INNER JOIN tbl_cliente \r\n				ON CLIENTE.N_CD_CODIGO = tbl_cliente.id_cliente_metropole,  \r\n\r\n	tbl_chamado_roteiro AS roteiro \r\n	INNER JOIN  tbl_chamado_visualizado AS chamado\r\n				ON roteiro.id_chamado = chamado.id_chamado			\r\n		\r\n	INNER JOIN	\r\n	tbl_chamado AS cha\r\n				ON cha.id_chamado = chamado.id_chamado\r\n	\r\n\r\n	\r\nWHERE \r\n	CLIENTE.N_CD_CODIGO = 8063\r\n	AND roteiro.data_solicitacao = \'2021-05-15\'\r\n	AND CLIENTE.N_CD_FILIAL = 1\r\n	AND CLIENTE.S_DSC_TPCLIENTE = \'C\'\r\n	and roteiro.id_empresa = 322\r\n	AND cha.nome_substituto = \'JOSE ANSELMO\'\r\n	--AND roteiro.id_chamado_roteiro = 153023746\r\n\r\nORDER BY cha.nome_substituto ASC'),
