(12,	'ValorAgregado ',	'SELECT SUM(N_DSC_VALOR_REPASSADO_AGREGADO) AS ValorAgregado FROM TBL_CHAMADOS_ROTAS WHERE N_CD_CHAMADO = 1097730'),
(13,	'N_DSC_VALOR_PONTO_METROPOLE',	'SELECT      \r\n                          Sum([ponto]) As Pontos,N_DSC_VALOR_PONTO_METROPOLE\r\n                       From\r\n                          (\r\n                             SELECT\r\n                                SUM(\r\n                                   CASE WHEN CHA.S_DSC_TIPO_CALCULO = \'H\'\r\n                                         THEN ROTA.N_DSC_VALOR_REPASSADO_HORAS_POR_PONTO_FUNCIONARIO_HORA + CASE WHEN Rota.S_DSC_FORA_PERIMETRO = \'S\' THEN 1 ELSE 0 END\r\n                  \r\n               \r\n                                   WHEN CHA.S_DSC_COBRAR_PONTOMAISKM = \'S\'\r\n                                      THEN ROTA.N_DSC_QTDE_HORAS_REPASSADA_FUNCIONARIO_HORA_NOVO\r\n                                   WHEN CHA.N_CD_UNIDADE = 3\r\n                                      THEN  N_DSC_VALOR_REPASSADO_SANTOS \r\n\r\n                                            + CASE WHEN ISNULL(ROTA.N_DSC_TOTAL_HPARADA,0) <= 0   \r\n                                               THEN 0\r\n                                               Else\r\n                                                 ISNULL(ROTA.N_DSC_TOTAL_HPARADA,0)*ISNULL(ROTA.N_DSC_PERCENTUAL_REPASSE_METROPOLE,0)\r\n                                                 -(ISNULL(ROTA.N_DSC_TOTAL_HPARADA,0)ISNULL(ROTA.N_DSC_PERCENTUAL_REPASSE_METROPOLE,0))(Rota.N_DSC_PERCENTUAL_PERICULOSIDADE/100)\r\n                                               End\r\n              \r\n                                   WHEN CHA.S_DSC_TIPO_TABELAVIP = \'ST\'  AND     ROTA.S_DSC_VIAGEM = \'S\'\r\n                                      THEN  ROTA.N_DSC_VALOR_REPASSADO_SANTOS/ROTA.N_DSC_VALOR_PONTO_METROPOLE\r\n                                   WHEN CHA.S_DSC_TIPO_TABELAVIP = \'ST\' \r\n                                      THEN  ROTA.N_DSC_QTDE_HORAS_REPASSADA_FUNCIONARIO_HORA_NOVO\r\n               \r\n                                            + CASE WHEN ISNULL(ROTA.N_DSC_TOTAL_HPARADA,0) <= 0   \r\n                                               THEN 0\r\n                                               Else\r\n                                                 ISNULL(ROTA.N_DSC_TOTAL_HPARADA,0)*ISNULL(ROTA.N_DSC_PERCENTUAL_REPASSE_METROPOLE,0)\r\n                                                  /Rota.N_DSC_VALOR_PONTO_METROPOLE\r\n\r\n                                               End\r\n                                   WHEN ROTA.S_DSC_VIAGEM = \'S\'\r\n                                      THEN  ROTA.N_DSC_QTDE_HORAS_REPASSADA_FUNCIONARIO_HORA_NOVO\r\n                                   Else\r\n                                      Rota.N_DSC_QTDE_PONTO_REAL\r\n                                      + CASE WHEN ROTA.S_DSC_RETORNO = \'S\' THEN 1 ELSE 0 END\r\n                                      + CASE WHEN ROTA.N_DSC_TOTAL_HPARADA <= 0 OR ROTA.N_DSC_VAL_HPARADA <= 0\r\n                                         THEN 0\r\n                                      Else\r\n                                         (CASE WHEN ROTA.N_DSC_TOTAL_HPARADA <= 0 THEN 1 ELSE ROTA.N_DSC_TOTAL_HPARADA END)\r\n                                         /(CASE WHEN ROTA.N_DSC_VAL_HPARADA <= 0 THEN 1 ELSE ROTA.N_DSC_VAL_HPARADA END)\r\n                                      End\r\n                                   End\r\n                                ) AS PONTO,ROTA.N_DSC_VALOR_PONTO_METROPOLE \r\n                             From\r\n                                TBL_CHAMADOS_ROTAS As Rota\r\n                                INNER JOIN TBL_CHAMADOS AS CHA ON\r\n                                   Rota.N_CD_CHAMADO = CHA.N_CD_CODIGO\r\n               \r\n                             Where     ROTA.S_DSC_EXCLUIDO = \'N\' AND     ROTA.N_CD_CHAMADO =  1097730 \r\n\r\n            \r\n               \r\n                             Group By     ROTA.S_DSC_VIAGEM,ROTA.N_DSC_QTDE_PONTO_FUNCIONARIO_NOVO,ROTA.N_DSC_QTDE_PONTO_REAL,ROTA.S_DSC_RETORNO,ROTA.N_DSC_TOTAL_HPARADA,ROTA.N_DSC_VAL_HPARADA,ROTA.N_DSC_VALOR_PONTO_METROPOLE \r\n                       ) AS P\r\n                    GROUP BY P.N_DSC_VALOR_PONTO_METROPOLE'),
